    const [recipe, setRecipe] = useState([]);

    setRecipe(data.Recipe);
    const updateSearch = () => {

        console.log({ meal });

    }

    return (
        <div className="col-4">
            <div className="card mb-3 mt-3">
                <div className="row">
                    <div className="col-5">
                        <img className={style.image} src={image} alt="" />
                    </div>
                    <div className="col-7">
                        <div className="row">
                            <div className="col-12">
                                <div className={style.title}>{title}</div>
                            </div>
                        </div>



                        <div className="row"><div className="col-12"><div className={style.meal}><p>{meal}</p></div></div></div>
                        <div className="row"><div className="col-12"><div className={style.diet}><p>{diet}</p></div></div></div>
                        <div className="row"><div className="col-12"><div className={style.cuisine}><p>{cuisine} cuisine</p></div></div></div>
                        <div className="row"><div className="col-12"><div className="button" value={idUri} onClick={updateSearch} ><Link className="nav-link" to="/details">Taste It</Link></div></div></div>

                        {recipe.map(Recipe => (<Details
                            idUri={Recipe.uri}
                            title={Recipe.title}
                            calories={Recipe.calories}
                            image={Recipe.image}
                            ingredients={Recipe.ingredients}
                            cuisine={Recipe.cuisine}
                            diet={Recipe.diet}
                            meal={Recipe.meal}



                        />))}







                    </div>
                </div>
            </div>
        </div>
    );
}

export default Recipe;
